import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators, FormBuilder } from '@angular/forms';
import { Router } from '@angular/router';
import { actualizarProducto, ObjetoProducto } from '../../clases/producto/producto';
import { ProductoService } from '../../servicios/producto/producto.service';

@Component({
  selector: 'app-actualizar-producto',
  templateUrl: './actualizar-producto.component.html',
  styleUrls: ['./actualizar-producto.component.css']
})
export class ActualizarProductoComponent {

  formulario = new FormGroup({
    id_producto: new FormControl ('',Validators.required),
    nombre_producto: new FormControl ('',Validators.required),
    descripcion: new FormControl('',Validators.required),
    valor_venta: new FormControl('',Validators.required),
    cantidad_stock: new FormControl('', Validators.required),
    Estado: new FormControl('', Validators.required),
  })

  Producto: ObjetoProducto[]=[];
  listarproductos: ObjetoProducto[]=[];

  constructor(private ProductoService: ProductoService, private formBuilder:FormBuilder, private router:Router){ }

  actualizar_producto(form:any){
    console.log("Estoy dentro de actualizar productos")
    const producto:actualizarProducto={
      Id_Producto:form.Id_Producto,
      nombre_producto:form.nombre_producto,
      descripcion:form.descripcion,
      valor_venta:form.valor_venta,
      cantidad_stock:form.cantidad_stock,
      Estado:form.Estado
    }

    console.log("Va a disparar el servicio");
    this.ProductoService.create(producto).subscribe({
      next:()=>{
        alert("Usuario registrado")
      },
      error:()=>{
        alert("Usuario no registrado")
      }

    })

  }
}
<section class="form-register">
    <h4>Actualizar Producto</h4>

  <form [formGroup]="formulario" (ngSubmit)="actualizar_producto(formulario.value)">

    <div class="form-group">
      <input class="controls" type="text" placeholder="id_producto" formControlName="Id_Producto">
    </div>

    <div class="form-group">
      <input class="controls" type="text" placeholder="Nombre producto" formControlName="nombre_producto">
    </div>

    <div class="form-group">
      <input class="controls" type="text" placeholder="descripcion" formControlName="descripcion">
    </div>

    <div class="form-group">
      <input class="controls" type="text" placeholder="valor venta" formControlName="valor_venta">
    </div>

    <div class="form-group">
      <input class="controls" type="text" placeholder="Cantidad stock" formControlName="cantidad_stock">
    </div>


    <div class="form-group">
      <button class="botons" type="submit"  data-mdb-ripple-color="dark">Registrar</button>
    </div>
    <div class="form-group">
      <a href="registrarproducto" class="right-link">¿Agregar Productos?</a>
    </div>
  </form>
</section>
