import { FormGroup, Validators, FormBuilder, Form, FormControl } from '@angular/forms';
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';

//Objetos y clases

import { ObjetoUsuario, CrearUsuario } from 'src/app/clases/usuario/usuario';
import { RegistroService } from 'src/app/servicios/registro.service';



@Component({
  selector: 'app-registro',
  templateUrl: './registro.component.html',
  styleUrls: ['./registro.component.css']
})

export class RegistroComponent implements OnInit{

formulario: FormGroup;

  //Objeto grupo
  Usuario: ObjetoUsuario[]=[];
  listagrupos: ObjetoUsuario[]=[];


 /* Capturar datos del formulario Grupos */
  constructor(private Servicio_Registro: RegistroService, private fb:FormBuilder, private router:Router) {
    this.formulario = this.fb.group({
        nombre_usuario: new FormControl ('',Validators.required),
        correo: new FormControl('',Validators.required),
        clave: new FormControl('',Validators.required),
    })
   }
  ngOnInit(): void {
    //throw new Error('Method not implemented.');
  }

/*   escuchandogrupos(){
    this.servicio_grupo.getAll().subscribe(response=>{
      this.listagrupos=response.cursos;
  })
    }
 */
    Registrar_usuario(){
    console.log("Estoy adentro de registro usuario")
    const usuario: CrearUsuario={
      nombre_usuario:this.formulario.value.nombre_usuario,
      correo:this.formulario.value.correo,
      clave:this.formulario.value.clave
    }

    console.log("Va a disparar el servicio");
    this.Servicio_Registro.create(usuario).subscribe({
      next:()=>{
        alert("Usuario registrado")
      },
      error:()=>{
        alert("Usuario no registrado")
      }

    })

  }}
<header class="p-3 bg-dark text-white">
    <div class="container">
      <div class="d-flex flex-wrap align-items-center justify-content-center justify-content-lg-start">
        <a href="/" class="d-flex align-items-center mb-2 mb-lg-0 text-white text-decoration-none">
          <svg class="bi me-2" width="40" height="32" role="img" aria-label="Bootstrap"><use xlink:href="#bootstrap"></use></svg>
        </a>

        <ul class="nav col-12 col-lg-auto me-lg-auto mb-2 justify-content-center mb-md-0">
          <li><a href="home" class="nav-link px-2 text-secondary">Inicio</a></li>
          <li><a href="/" class="nav-link px-2 text-white">Bebidas</a></li>
          <li><a href="/" class="nav-link px-2 text-white">Snacks</a></li>
          <li><a href="agregarproducto" class="nav-link px-2 text-white">Reservaciones</a></li>
          <li><a href="actualizarproducto" class="nav-link px-2 text-white">Sobre Nosotros</a></li>
        </ul>

        <div class="text-end">
          <form [formGroup]="formulario">
          <a href="login" type="button" class="btn btn-outline-light me-2">Iniciar Sesión</a>
          <a href="registro" type="button" class="btn btn-warning">Registrarme</a>
        </form>
        </div>
      </div>
    </div>
  </header>

<section class="form-register">
    <h4>Formulario Registro</h4>

    <form [formGroup]="formulario" (ngSubmit)="Registrar_usuario()">

        <div >
            <input class="controls" type="text" placeholder="Nombre" formControlName="nombre_usuario">
        </div>
        <div >
            <input class="controls" type="text" placeholder="Correo Electronico" formControlName="correo">
        </div>
        <div >
            <input class="controls" type="password" placeholder="clave" formControlName="clave">
        </div>
        <div class="form-check">
            <input class="form-check-input" type="checkbox" value="" id="flexCheckDefault">
            <label class="form-check-label" for="flexCheckDefault">
              Aceptar Terminos y Condiciones
            </label>
          </div>
        <div >
            <button class="botons" type="submit"  data-mdb-ripple-color="dark">Registrarme</button>
        </div>
        <div >
            <a href="login" class="right-link">¿Ya tengo Cuenta? Inicia sesión aqui</a>
        </div>
    </form>

    <!-- <input class="controls" type="email" name="correo" id="correo" placeholder="Correo">
        <input class="controls" type="text" name="apellidos" id="apellidos" placeholder="Nombre Usuario">
        <input class="controls" type="password" name="correo" id="correo" placeholder="Contraseña">
        <p>Estoy de acuerdo con <a href="#">Terminos y Condiciones</a></p>
        <input class="botons" type="submit" value="Registrar">
        <p>
            <a href="login"></a>
        </p> -->
</section>
